def fibonacci(n):
    """Generates the Fibonacci sequence up to n terms."""
    a, b = 0, 1
    for _ in range(n):
        yield a
        a, b = b, a + b

if __name__ == "__main__":
    for number in fibonacci(10):
        print(number)

